            String preRelease = matcher.group(5);
            String build = matcher.group(6);

            if (compareSign.equals(EQ.asString()) && isX(patch)) {
                compareSign = EMPTY;
            }

            if (!compareSign.isEmpty() && isX(patch)) {
                if (isX(minor)) {
                    minor = 0;
                }
                patch = 0;
                if (compareSign.equals(GT.asString())) {
                    compareSign = GTE.asString();

                    if (isX(minor)) {
                        major = major + 1;
                        minor = 0;
                    } else {
                        minor = minor + 1;
