                    position.set(Position.KEY_OBD_SPEED, buf.readUnsignedShortLE());
                    break;
                case 0x98:
                    position.set(Position.KEY_FUEL_USED, buf.readUnsignedShortLE());
                    break;
                case 0x99:
                    position.set(Position.KEY_RPM, buf.readUnsignedShortLE());
