        paramCount = buf.readUnsignedByte();
        for (int j = 0; j < paramCount; j++) {
            boolean extension = buf.getUnsignedByte(buf.readerIndex()) == 0xFE;
            int id = extension ? buf.readUnsignedShort() : buf.readUnsignedByte();
            switch (id) {
                case 0x02:
