            int endIndex = buf.readUnsignedByte() + buf.readerIndex();
            int key = buf.readUnsignedByte();

            if (keys.contains(key)) {
                if (!hasLocation) {
                    getLastLocation(position, null);
                }
                positions.add(position);
                keys.clear();
                hasLocation = false;
                position = new Position(getProtocolName());
            }
            keys.add(key);

