                position.set(Position.KEY_ODOMETER, buf.readUnsignedIntLE());
                break;
            case 0x0D:
                position.set(Position.KEY_HOURS, buf.readUnsignedIntLE() * 1000);
                break;
            case 0x14:
                position.set(Position.KEY_OUTPUT, buf.readUnsignedByte());
                break;
