        if (sentence.endsWith("$")) {
            checksum = sentence.substring(sentence.length() - 1 - 4, sentence.length() - 1);
        } else {
            checksum = sentence.substring(sentence.length() - 4);
        }
        channel.writeAndFlush(new NetworkMessage("+SACK:" + checksum + "$", remoteAddress));
    }

    return result;
}

