            case 0xD4:
                position.set(Position.KEY_BATTERY_LEVEL, buf.readUnsignedByte());
                break;
            case 0xD5:
                if (length == 2) {
                    position.set(Position.KEY_BATTERY, buf.readUnsignedShort() * 0.01);
                } else {
                    int count = buf.readUnsignedByte();
