                } else {
                    while (buf.readerIndex() < endIndex) {
                        int extendedLength = buf.readUnsignedShort();
                        int extendedType = buf.readUnsignedShort();
                        switch (extendedType) {
                            case 0x0001:
                                position.set("fuel1", buf.readUnsignedShort() * 0.1);
